import React  ,{ useState} from 'react'
import "./mix.css"
import { NavLink } from 'react-router-dom'

function Register() {
  const [pasShow, setpasShow] = useState(false)
  const [cpasShow, setCpasShow] = useState(false)

  const [inpval, setinpval] = useState({
    fname:"",
    email:'',
    password:'',
    cpassword:''
  });
  console.log(inpval)
  const setval = (e)=>{
    const {name,value} = e.target
    // console.log(e.target.value)
    setinpval(()=>{
      return{
        ...inpval, 
        [name]:value
    }})
  }
  const addUserdata =(e)=>{
    e.prveventDefult()
    const {fname,email,password,cpassword} = inpval
    if(fname === ""){
      alert("please inter your name ")
    }else if(email === ""){
      alert("please inter your email")
    }

  }

  return (
    <>
       <section>
      <div className='form_data'>
        <div className='form_heading'>
          <h1>Welcome Back ,  SignUp</h1>
          <p style={{textAlign:'center'}}>Hi, we are you glad you will be using . Project Cloud to manage <br />
           your tasks! We hope that you will get Like it.</p>
        </div>
        <form>
        <div className='form_input'>
            <label>Name</label>
            <input type='text' name='fname' onChange={setval} value={inpval.fname} id='fname' placeholder='Enter your Name'/>
          </div>
          <div className='form_input'>
            <label>Email</label>
            <input type='email' name='email' onChange={setval} value={inpval.email} id='email' placeholder='Enter your email'/>
          </div>
          <div className='form_input'>
            <label htmlFor=''>Password</label>
            <div className='two'>
            <input type={!pasShow ? "password" : "text"} onChange={setval} value={inpval.password} name='password' id='password' placeholder='Enter your password'/>
            <div className='showpass' onClick={()=> setpasShow(!pasShow)}>
              {!pasShow ? "Show" : "Hide"}
            </div>
            </div>
          </div>
          <div className='form_input'>
            <label htmlFor=''>Confirm Password</label>
            <div className='two'>
            <input type={!cpasShow ? "password" : "text"} onChange={setval} name='cpassword' value={inpval.cpassword} id='cpassword' placeholder='Enter your confirm password'/>
            <div className='showpass' onClick={()=> setCpasShow(!cpasShow)}>
              {!pasShow ? "Show" : "Hide"}
            </div>
            </div>
          </div>
          <button className='btn' onClick={addUserdata}>SignUp</button>
          <p>Don't have an Account ? <NavLink to="/"> Login </NavLink>  </p>
        </form>
      </div>
    </section>
    </>
  )
}

export default Register
